import React, {useEffect, useRef, useState} from 'react'
import MainLayout from '../layouts/MainLayout'
import axios from "axios"
import { toast } from 'react-toastify';
import { ComponentToPrint } from '../components/ComponentToPrint';
import { useReactToPrint } from 'react-to-print';


function POSPage() {

  const [products, setProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [cart, setCart] = useState([]);
  const [totalAmount, setTotalAmount] = useState(0);

  const toastOptions = {
    autoClose: 400,
    pauseOnHover: true,
  }

  const fetchProducts = async() => {
    setIsLoading(true);
    const result = await axios.get('products');
    setProducts(await result.data);
    setIsLoading(false);
  }

  const addProductToCart = async(product) =>{
    // check if the adding product exist
    let findProductInCart = await cart.find(i=>{
      return i.id === product.id
    });

    if(findProductInCart){
      let newCart = [];
      let newItem;

      cart.forEach(cartItem => {
        if(cartItem.id === product.id){
          newItem = {
            ...cartItem,
            quantity: cartItem.quantity + 1,
            totalAmount: cartItem.price * (cartItem.quantity + 1)
          }
          newCart.push(newItem);
        }else{
          newCart.push(cartItem);
        }
      });

      setCart(newCart);
      toast(`Added ${newItem.name} to cart`,toastOptions)

    }else{
      let addingProduct = {
        ...product,
        'quantity': 1,
        'totalAmount': product.price,
      }
      setCart([...cart, addingProduct]);
      toast(`Added ${product.name} to cart`, toastOptions)
    }

  }

  const removeProduct = async(product) =>{
    const newCart =cart.filter(cartItem => cartItem.id !== product.id);
    setCart(newCart);
  }

  const componentRef = useRef();

  const handleReactToPrint = useReactToPrint({
    content: () => componentRef.current,
  });

  const handlePrint = () => {
    handleReactToPrint();
  }

  useEffect(() => {
    fetchProducts();
  },[]);

  useEffect(() => {
    let newTotalAmount = 0;
    cart.forEach(icart => {
      newTotalAmount = newTotalAmount + parseInt(icart.totalAmount);
    })
    setTotalAmount(newTotalAmount);
  },[cart])


  return (
    <MainLayout>
      <div className='w-full flex flex-row'>
        <div className='w-[60%]'>
          {isLoading ? (
            'Loading'
          ) : (
            <div className='flex flex-wrap'>
              {products.map((product, key) => (
                <div key={key} className='w-full sm:w-1/2 md:w-1/3 lg:w-1/4 xl:w-1/4 p-4'>
                  <div className='border-8 p-4' onClick={() => addProductToCart(product)}>
                    <p className='text-center'>{product.name}</p>
                    <img src={product.image} alt={product.name} className='w-full h-auto rounded-lg' />
                    <p>₱{product.price}</p>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/*--------------------------- CART LIST -------------------------*/}

        <div className='w-[40%] min-h-screen'>

          <div style={{display: "none"}}>
                <ComponentToPrint cart={cart} totalAmount={totalAmount} ref={componentRef}/>
          </div>

            <div className=''>
              <table className='w-full'>
                <thead className=' '>
                  <tr className='text-white bg-black h-[3.5rem]'>
                    <td className='px-2'>#</td>
                    <td className='px-2'>Product Name</td>
                    <td className='px-2'>Price</td>
                    <td className='px-2'>Qty</td>
                    <td className='px-2'>Amount</td>
                    <td className='px-2'>Action</td>
                  </tr>
                </thead>
                
                <tbody>
                  { cart ? cart.map((cartProduct, key) =>
                  <tr key={key} className={key % 2 === 0 ? 'bg-gray-200 h-[3.5rem]' : 'bg-white h-[3.5rem]'}>
                    <td>{cartProduct.id}</td>
                    <td>{cartProduct.name}</td>
                    <td>{cartProduct.price}</td>
                    <td>{cartProduct.quantity}</td>
                    <td>₱{cartProduct.totalAmount}</td>
                    <td>
                      <button className='border text-white bg-red-600 rounded-lg p-2' onClick={() => removeProduct(cartProduct)}>Remove</button>
                    </td>
                  </tr>

                  )

                  : 'No Item in Cart'
                
                }
                </tbody>
              </table>
              <h2 className='bg-black text-white text-2xl p-4 text-center'>Total Amount: <span className='font-bold'>₱{totalAmount}</span></h2>
            </div>

                <div className='mt-3'>
                  { totalAmount !== 0 ? 
                  <div> 
                    <button className='' onClick={handlePrint}>
                      Pay Now
                    </button>
                  </div>
                   : 'Please add a product to the cart.'}
                </div>


        </div>
      </div>
    </MainLayout>
  );
};

export default POSPage;
